#define ID_WINDOW_0     	(GUI_ID_USER + 0x00)

#define UNLOCK_ID_STR_1     (GUI_ID_USER + 0x01)
#define UNLOCK_ID_STR_2     (GUI_ID_USER + 0x02)
#define UNLOCK_ID_STR_3     (GUI_ID_USER + 0x03)
#define UNLOCK_ID_IMG	    (GUI_ID_USER + 0x04)
#define TITLE_ID 			(GUI_ID_USER + 0x05)

#define IMG_SET_ARROW_WIDTH		(8)	
#define IMG_SET_ARROE_HEIGH     (16)

