void get_one_image_and_char_coordinate(uint8_t cursor_index, GUI_RECT *rect)
{
	uint8_t data_width, sum_len;
	uint8_t one_image_and_data_with;
	uint8_t image_start_x, y;
	GUI_RECT rt;

	ssz_assert(cursor_index < 4);
	GUI_SetFont(get_font(60));
	data_width = GUI_GetStringDistX("1");
	
	one_image_and_data_with = data_width + IMG_SET_ARROW_WIDTH;
	sum_len = one_image_and_data_with*3 + IMG_SET_ARROW_WIDTH*2;

	image_start_x = (DISPLAY_WIDTH - sum_len)/2 + cursor_index*(one_image_and_data_with + IMG_SET_ARROW_WIDTH);
	y = 12;

	ui_rect_init_by_size(rt, image_start_x, y, one_image_and_data_with, DISPLAY_HEIGHT - 12);
	*rect = rt;
}


void get_image_coordinate(uint8_t cursor_index, GUI_RECT *rect)
{
	GUI_RECT rt, temp;

	get_one_image_and_char_coordinate(cursor_index, &rt);
	
	ui_rect_init_by_size(temp, rt.x0, rt.y0, IMG_SET_ARROW_WIDTH, DISPLAY_HEIGHT - 12);	

	*rect = temp;
}


void get_char_coordinate(uint8_t cursor_index, GUI_RECT *rect)
{
	GUI_RECT rt, temp;
	get_one_image_and_char_coordinate(cursor_index, &rt);

	ui_rect_init_by_size(temp, rt.x0 + IMG_SET_ARROW_WIDTH, rt.y0, 
		                 (rt.x1-rt.x0 + 1) - IMG_SET_ARROW_WIDTH, DISPLAY_HEIGHT - 12);	
	*rect = temp;
}


void put_select_arrow(uint8_t cursor_index)
{
	GUI_RECT rect;
	get_image_coordinate(cursor_index, &rect);
	WM_MoveChildTo(WM_GetDialogItem(ui_get_current_hwin(), UNLOCK_ID_IMG), rect.x0, 
		           rect.y0 + ((rect.y1 - rect.y0 +1) - IMG_SET_ARROE_HEIGH)/2+3);
}

static void put_select_password(uint8_t cursor_index, uint8_t data)
{
	GUI_RECT rect;
	char *p;
	int id[3] = {UNLOCK_ID_STR_1, UNLOCK_ID_STR_2, UNLOCK_ID_STR_3};
	get_char_coordinate(cursor_index, &rect);
	p = get_dynamic_string(kStrDynamic1);
	sprintf(p, "%d", data); 
	TEXT_SetText(WM_GetDialogItem(ui_get_current_hwin(), id[cursor_index]), p);
}

static void put_title(uint8_t title_index)
{
	WM_HWIN hWin;
	const char * ptr;
	hWin = WM_GetDialogItem(ui_get_current_hwin(),TITLE_ID);

	if(title_index == 0)
	{
		ptr = get_string(kStrInputPasswordToUnlock);
		TEXT_SetText(hWin,ptr);
	}
	else if(title_index == 1)
	{
		ptr = get_string(kStrSetPassword);
		TEXT_SetText(hWin,ptr);
	}
	
	
}

/*********************************************************************
*
*       Static code
*
***********************************************************************/
static void _cbDialog(WM_MESSAGE * pMsg) 
{
	uint8_t strlen;
	GUI_RECT rect; 
	const char *ptr;
	WM_HWIN hWin;
	int i, id[3] = {UNLOCK_ID_STR_1, UNLOCK_ID_STR_2, UNLOCK_ID_STR_3};
	
	switch (pMsg->MsgId) 
	{
		case WM_INIT_DIALOG:
			WINDOW_SetBkColor(pMsg->hWin, GUI_BLACK);

			//ptr = get_string(kStrInputPasswordToUnlock);
			//strlen = GUI_GetStringDistX(ptr);
			hWin = TEXT_CreateEx(2, 2, 100, 14, pMsg->hWin, 
				                 WM_CF_SHOW, GUI_TA_VCENTER|GUI_TA_LEFT,  TITLE_ID, get_string(kStrInputPasswordToUnlock));
			TEXT_SetFont(hWin, get_font(14));
			TEXT_SetBkColor(hWin, GUI_BLACK);
			TEXT_SetTextColor(hWin, GUI_WHITE);
			
			for(i=0; i<3; i++)
			{//str
				get_char_coordinate(i, &rect);
				hWin = TEXT_CreateAsChild(rect.x0, rect.y0+5, rect.x1 - rect.x0 + 1, rect.y1 - rect.y0 + 1, pMsg->hWin, id[i], 
					                      WM_CF_SHOW, "0", GUI_TA_VCENTER|GUI_TA_HCENTER);
				TEXT_SetFont(hWin, get_font(60));
				TEXT_SetBkColor(hWin, GUI_BLACK);
				TEXT_SetTextColor(hWin, GUI_WHITE);
			}
			

			//img
			get_image_coordinate(0, &rect);
			hWin = IMAGE_CreateEx(rect.x0, rect.y0, rect.x1 - rect.x0 + 1, (rect.y1 - rect.y0 + 1)/2, pMsg->hWin, 
				                  WM_CF_SHOW,  IMAGE_CF_AUTOSIZE, UNLOCK_ID_IMG);
			IMAGE_SetBitmap(hWin, get_image(kImgSetArrow));

			break;
	
		case WM_PAINT:
			GUI_SetColor(GUI_WHITE);
			GUI_SetFont(get_font(14));
				
			ptr = get_string(kStrReturn);
			strlen = GUI_GetStringDistX(ptr);
			ui_rect_init_by_size(rect, 0, DISPLAY_HEIGHT-16, strlen, 16);
			GUI_DispStringInRect(ptr, &rect, GUI_TA_LEFT|GUI_TA_VCENTER);

	
			ptr = get_string(kStrConfirm);
			strlen = GUI_GetStringDistX(ptr);
			ui_rect_init_by_size(rect, DISPLAY_WIDTH-strlen, DISPLAY_HEIGHT-16, strlen, 16);
			GUI_DispStringInRect(ptr, &rect, GUI_TA_RIGHT|GUI_TA_VCENTER);
			break; 
			
		default:
			  WM_DefaultProc(pMsg);
			  break;
	}

}

