WM_HWIN app_scr_class5_create(type_MsgBody4UICtrlMsg *msg) 
{
	WM_HWIN hWin;	
	hWin = GUI_CreateDialogBox(_aDialogCreate, GUI_COUNTOF(_aDialogCreate), _cbDialog, WM_HBKWIN, 0, 0);
	//ui_clear_init_once_flag();
	return hWin;
}

int app_scr_class5_update(WM_HWIN hwin, type_MsgBody4UICtrlMsg *msg)
{
	put_title(msg->SItem.DataValArray[1]);
	put_select_password(0,msg->ditem.DataValArray[1]);
	put_select_password(1,msg->ditem.DataValArray[2]);
	put_select_password(2,msg->ditem.DataValArray[3]);
	put_select_arrow(msg->CursorIndex.CursorIndex);

 	return 0;	
}

void app_scr_class5_destroy(WM_HWIN win_id) 
{
	GUI_EndDialog(win_id, 0);
}


